        width:240,
        height:50,
        position: 'absolute',
        top: 320,
        left: 80,
        borderWidth: 1,
        borderColor: 'lightskyblue',
        padding: 8,
        

    },
    input2: {
        width:240,
        height:50,
        position: 'absolute',
        top: 430,
        left: 80,
        borderWidth: 1,
        borderColor: 'lightskyblue',
        padding: 8,
        

    },
    registerButton: {
        width: "100%",
        backgroundColor: "lightskyblue",
        height: 50,
        bottom: 75,
    },
    signinButton: {
        width: "100%",
        backgroundColor: "navy",
